function generateRandomPosts(){$.getJSON("/search.json",function(a){console.log("[search.json loaded for random posts]");var e=a.length,s=a,i=[],t=0,c=8,l=$("#random_posts");for(l.append("");c>t;){var r=Math.floor(Math.random()*e);if("-1"==i.indexOf(r)){var d=s[r].href,n=s[r].title,o=s[r].tags;l.append(t==c-1?'<div class="article-medium"><div class="article-header"><img  src="http://i2.wp.com/media.radiobucket.com/images/play.png" alt="img" /></div><div class="article-content"><h2><a href="'+d+'" title="'+n+'">'+n+'</a></h2></div><div class="article-footer"><a class="left">Genre : '+o+' </a><a href="'+d+'" class="right">TuneIn<span class="icon-text">&#9656;</span></a><div class="clear-float"></div></div></div>':'<div class="article-medium"><div class="article-header"><img  src="http://i2.wp.com/media.radiobucket.com/images/play.png" alt="img" /></div><div class="article-content"><h2><a href="'+d+'" title="'+n+'">'+n+'</a></h2></div><div class="article-footer"><a class="left">Genre : '+o+' </a><a href="'+d+'" class="right">TuneIn<span class="icon-text">&#9656;</span></a><div class="clear-float"></div></div></div>'),i.push(r),t++}}})}$(document).ready(function(){generateRandomPosts()});